class Solution:
    def getRow(self, rowIndex: int) -> List[int]:
        if rowIndex == 0:
            return[1]
        elif rowIndex == 1:
            return [1, 1]
        row = [1,1]
        for i in range(2, rowIndex+1):
            new_row = [1] * (i+1)
