        dp = [False] * (len(s)+1)
        dp[0] = True

        maxLen = 0
        for word in wordDict:
            maxLen = max(maxLen, len(word))

        for i in range(1, len(s)+1):
            for j in range(max(i-maxLen, 0), i):
                if dp[j] and s[j:i] in wordDict:
                    dp[i] = True
                    break
        return dp[len(s)]

