class MinStack:

    def __init__(self):
        """
        initialize your data structure here.
        """
        self.stack = []  #栈
        self.minIdx = -1  #最小元素下标

    def push(self, x: int) -> None:       
        self.stack.append(x)
        if self.minIdx == -1:
            self.minIdx = 0
        elif x  None:
