            self.minIdx = -1
        #若弹出元素是最小元素，则需更新最小下标
        elif len(self.stack)-1 == self.minIdx:
            self.minIdx = self.stack.index(min(self.stack[:-1]))        
        self.stack.pop()

    def top(self) -> int:
        return self.stack[-1]

    def getMin(self) -> int:
        return self.stack[self.minIdx]

