        #长度>=3
        else:
            mid = n // 2  #取中点
            #若中点比两侧大，直接去中点
            if nums[mid-1]  nums[mid+1]:
                return mid
            #若左侧大，说明左侧可能存在山峰，递归处理左侧数组
            elif nums[mid-1] > nums[mid]:
                return self.findPeakElement(nums[:mid])
            #否则递归处理右侧
            else:
                return mid+1 + self.findPeakElement(nums[mid+1:])

