class Solution:
    def findPeakElement(self, nums: List[int]) -> int:
        low, high = 0, len(nums)-1
        mid = 0
        while low  nums[low] else low
                break
            #若mid是山峰
            if nums[mid-1]  nums[mid+1]:
                break
            #左侧更大，对左侧二分
            elif nums[mid-1] > nums[mid]:
                high = mid-1
            #否则右侧
            else:
                low = mid+1
        #若区间缩减为一个元素，即为所求
        if low == high:
            return low
        #否则mid为所求
        return mid

