class Solution:
    def isIsomorphic(self, s: str, t: str) -> bool:
        dict_s = [-1] * 256
        dict_t = [-1] * 256
        for i in range(len(s)):
            if dict_s[ord(s[i])] == -1:
                dict_s[ord(s[i])] = i
            if dict_t[ord(t[i])] == -1:
                dict_t[ord(t[i])] = i
            if dict_s[ord(s[i])] != dict_t[ord(t[i])]:
                return False
        return True

