 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    vector> findLeaves(TreeNode* root) {
        vector> res;
        dfs(root, res);
        return res;
    }
    //辅助函数,深度遍历
    int dfs(TreeNode* root, vector>& res) {
        if (!root) return 0;  //空节点,高度为0
        int level = max(dfs(root->left, res), dfs(root->right, res)) + 1;  //每个节点高度为子树的最大高度+1,递归求
        if (level > res.size()) res.push_back(vector());  //每当高度超过res长度时,res扩张
        //res从0开始,而level从1开始
        res[level-1].push_back(root->val);  //每个节点写入
        return level;
    }
};

